import gql from 'graphql-tag';

export const themeQuery = gql`
  query getAllUserEstimates(
    $historic: Boolean!
    $userId: ID!
    $cursor: Int
    $limit: Int
    $period: Period!
  ) {
    getAllUserEstimates(
      historic: $historic
      userId: $userId
      cursor: $cursor
      limit: $limit
      period: $period
    ) {
      next
      count
      data {
        corporate {
          id
          name
          slug
        }
        report {
          year
          quarter
          reportDate
          reportDateApproximate
        }
        estimates {
          value
          year
          quarter
          createdAt
          estimateType {
            actual
            value
            currency(period: $period)
            name
          }
        }
      }
    }
  }
`;

export default themeQuery;
